import React from "react";
import "./App.css";
import Button from "./Button";
import base from "./base";
import buttons from "./buttons";

class App extends React.Component {
  state = {
    buttons: {},
  };

  usePersistedState(key, defaultValue) {
    // Some magic
    const [state, setState] = React.useState(
      JSON.parse(localStorage.getItem(key)) || defaultValue
    );
    useEffect(() => {
      localStorage.setItem(key, JSON.stringify(state));
    }, [key, state]);
    return [state, setState];
  }

  componentDidMount() {
    this.setState({ buttons });
    /* base.syncState(`${this.props.match.params.nameId}`, {
      context: this,
      state: "buttons",
    }); */
  }

  /* componentDidUpdate() {
    this.clickButton();
  } */

  clickButton = (key) => {
    const buttons = { ...this.state.buttons };
    buttons[`button${key}`].condition = !buttons[`button${key}`].condition;
    this.setState({ buttons });
  };

  render() {
    var array = new Array(100);
    for (var i = 1; i <= 100; i++) {
      array.push(i);
    }
    //console.log(this.state.buttons[`button${100}`].condition);
    const buttonLoop = array.map((number) => {
      return (
        <Button
          key={number}
          index={number}
          addButton={this.addButton}
          clickButton={this.clickButton}
          condition={buttons[`button${number}`].condition}
        ></Button>
      );
    });
    return (
      <div>
        <header>
          <h1>100 Days to Habit</h1>
        </header>
        <div>
          <div className="button-container">{buttonLoop}</div>
        </div>
      </div>
    );
  }
}

export default App;
