import React from "react";
import "./App.css";
import Button from "./Button";
import base from "./base";
import buttons from "./buttons";
import BtnContainer from "./BtnContainer";

class App extends React.Component {
  state = {
    buttons: {},
  };

  componentDidUpdate() {
    const { params } = this.props.match;
    this.ref = base.syncState(`${params.nameId}/buttons`, {
      context: this,
      state: "buttons",
    });
  }

  buttonUpdate = () => {
    const { params } = this.props.match;
    this.ref = base.syncState(`${params.nameId}/buttons`, {
      context: this,
      state: "buttons",
    });
  };

  addButtons = () => {
    this.setState({ buttons });
  };

  clickButton = (key) => {
    const buttons = { ...this.state.buttons };
    buttons[`button${key}`].condition = !buttons[`button${key}`].condition;
    this.setState({ buttons });
  };

  render() {
    /* var array = new Array(100);
    for (var i = 1; i <= 100; i++) {
      array.push(i);
    }
    //console.log(this.state.buttons[`button${100}`].condition);
    const buttonLoop = array.map((number) => {
      return (
        <Button
          key={number}
          index={number}
          clickButton={this.clickButton}
          condition={buttons[`button${number}`].condition}
        ></Button>
      );
    }); */
    return (
      <div>
        <header>
          <h1>100 Days to Habit</h1>
        </header>
        <BtnContainer
          addButtons={this.addButtons}
          clickButton={this.clickButton}
          buttonUpdate={this.buttonUpdate}
        />
      </div>
    );
  }
}

export default App;
