import React from "react";
import "./App.css";
import Button from "./Button";
import base from "./base";

class App extends React.Component {
  state = {
    buttons: {},
    condition: {},
  };

  componentDidMount() {
    const localStorageRef = localStorage.getItem("params.nameId");
    if (localStorageRef) {
      this.setState(JSON.parse(localStorageRef));
    }
    this.ref = base.syncState(`${this.props.match.params.nameId}`, {
      context: this,
      state: "buttons",
    });
  }

  addButtons = (button) => {
    const buttons = { ...this.state.buttons };
    buttons[`button${Date.now()}`] = button;
    this.setState({ buttons });
  };

  handleClick() {
    this.setState({
      condition: !this.state.condition,
    });
  }

  render() {
    var array = new Array(100);
    for (var i = 1; i <= 100; i++) {
      array.push(i);
    }
    const buttonLoop = array.map((number) => {
      return (
        <Button
          key={number}
          index={number}
          handleClick={this.handleClick}
        ></Button>
      );
    });
    return (
      <html>
        <header>
          <h1>100 Days to Habit</h1>
        </header>
        <body>
          <div className="button-container">{buttonLoop}</div>
        </body>
      </html>
    );
  }
}

export default App;
